import "./App.css";
import { useState, useEffect } from "react";
import Header from "./Comp/Header";
import Book from "./Comp/Book";
import Shelf from "./Comp/Shelf";
import Shelves from "./Comp/Shelves";
import * as BooksAPI from "./BooksAPI";
import { BrowserRouter as Router } from 'react-router-dom'

function App() {

  const [showSearchPage, setShowSearchpage] = useState(false);
  
  //هذي للكويري حقت السيرش
  const [searchQuery, setSearchQuery] = useState("");
  const [searchBooks, setSearchBooks] = useState([]);

  //بوك حنحط فيه الكتب من الاي بي اي 
  const [books, setBooks] = useState([]);


  // ميثود السيرش
  const handleSearchTextChange = event => {
    if (searchQuery.length !== 0) {
      BooksAPI.search(searchQuery).then(searchedBooks => {
        if (!searchedBooks.error) {
          BooksAPI.getAll().then(myBooks => {
            setSearchBooks(setDefaultShelves(searchedBooks, myBooks));
          });
        } else {
          setSearchBooks([]);
        }
      });
    } else if (searchQuery.length === 0) {
      setSearchBooks([]);
    }
  };

  const setDefaultShelves = (searchedBooksLocal, myBooks) => {
    return searchedBooksLocal.map(book => {
      for (let i = 0; i < myBooks.length; i++) {
        if (myBooks[i].id === book.id) {
          return { ...book, shelf: myBooks[i].shelf };
        }
      }
      return { ...book, shelf: "none" };
    });
  };

  // ميثود تحط اللي في الاي بي اي لبوكس 
  useEffect(() => {
    BooksAPI.getAll().then(booksFromApi => {
      setBooks(booksFromApi);
      console.log(books);
    });
  }, []);



  useEffect(() => {
    handleSearchTextChange();
  }, [searchQuery]);






  return (
    <div className="app">
      <Router>

      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}//هنا نحط امر انو يمسح الهيستوري
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
                value={searchQuery}
                onChange={e => setSearchQuery(e.target.value)}
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
              {searchBooks.map(b => (
                <li key={b.id}>
                  <Book book={b} setBooks={setBooks} />
                </li>
              ))}

            </ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <Header></Header>
          <div className="list-books-content">
              <Shelves books={books} setBooks={setBooks} />
              
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
      
      </Router>
    </div>
  );
}

export default App;
