import React from 'react';
import Shelves from './Shelves';
import Book from './Book';
import { Link } from "react-router-dom";

const Search = ({ showSearchPage, setShowSearchpage, searchQuery, setSearchQuery, searchBooks, setBooks, books, setSearchBooks }) => {

    
    return (
        <div>
            {showSearchPage ? ( //صفحه السيرش 
                <div className="search-books">
                    <div className="search-books-bar">
                        <a className="close-search" onClick={() => setShowSearchpage(!showSearchPage) && setSearchBooks([])}  >
                            Close 
                        </a>
                        <div className="search-books-input-wrapper">
                            <input
                                type="text"
                                placeholder="Search by title, author, or ISBN"
                                value={searchQuery}
                                onChange={e => setSearchQuery(e.target.value)}
                            />
                        </div>
                    </div>
                    <div className="search-books-results">
                        <ol className="books-grid">
                            {searchBooks.map(b => (
                                <li key={b.id}>
                                    <Book book={b} setBooks={setBooks} />
                                </li>
                            ))}

                        </ol>
                    </div>
                </div>
            ) : (
                <div className="list-books">
                    <div className="list-books-title">
                        <h1>MyReads</h1>
                    </div>
                    <div className="list-books-content">
                        <Shelves books={books} setBooks={setBooks} />

                    </div>
                    <div className="open-search">
                            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
                    </div>
                </div>
            )}
        </div>

    )



}
export default Search;
